--
-- 資料表結構 `product`
--

CREATE TABLE `product` (
  `order_id` int NOT NULL,
  `game_id` varchar(255) NOT NULL,
  `game_des` varchar(255) NOT NULL,
  `type` int NOT NULL,
  `price` int NOT NULL,
  `stock` int NOT NULL,
  `payment` int NOT NULL,
  `created_at` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

--
-- 傾印資料表的資料 `product`
--

INSERT INTO `product` (`order_id`, `game_id`, `game_des`, `type`, `price`, `stock`, `payment`, `created_at`) VALUES
(1, '卡坦島（Settlers of Catan）', '卡坦島（Settlers of Catan）是一款由德國設計師克勞斯·特伯設計的桌上遊戲，於1995年首次發布。這款遊戲融合了資源管理、策略規劃和交易等元素，通常需要3到4位玩家參與。玩家扮演定居者，競相在一個由六邊形地塊組成的島嶼上建立定居點和道路，並通過生產和交易資源（木材、磚塊、羊毛、小麥和礦石）來積累勝利點數。', 1, 760, 50, 1, '2024-07-18 09:15:44'),
(5, '12', '12', 12, 12, 12, 0, '2024-07-18 10:11:05'),
(7, 'sfsfw', 'fdgdg', 1, 453645364, 10, 1, '2024-07-18 11:05:40'),
(8, '石器時代（Stone Age）', '石器時代（Stone Age）是一款由德國設計師Michael Tummelhofer創作的桌上遊戲，於2008年由Hans im Glück首次出版發行。這款遊戲以策略、資源管理和工人放置為核心機制，適合2到4名玩家參與。', 1, 330, 5, 1, '2024-07-18 11:08:30'),
(9, '禁區（Forbidden Island）', '《禁區》（Forbidden Island）是一款合作式桌上遊戲，由美國遊戲設計師Matt Leacock設計，於2010年由Gamewright出版發行。這款遊戲適合2到4名玩家參與，每局時間約30分鐘。', 1, 500, 30, 0, '2024-07-18 11:13:12'),
(10, '禁忌之島（Forbidden Desert）', '《禁忌之島》（Forbidden Desert）是一款由美國遊戲設計師Matt Leacock設計的合作式桌上遊戲，由Gamewright出版發行。這款遊戲於2013年首次發行，是《禁區》（Forbidden Island）的續作。', 2, 500, 50, 0, '2024-07-18 11:23:01'),
(11, '多米諾（Dominoes）', '多米諾（Dominoes）是一種常見的桌上遊戲，玩家會使用長方形的小塊（稱為骨牌）來進行遊戲。每個骨牌都有兩端，每一端都有一個數字或點數。遊戲的目標通常是將你手中的骨牌依照特定的規則或遊戲方式，一一放置出去，直到手中沒有骨牌為止。', 2, 500, 40, 10, '2024-07-18 13:09:32');

